@NgModule({
  imports: [IonicModule, PlanCreationPageRoutingModule, SharedModule],
  declarations: [PlanCreationPage],
})
export class PlanCreationPageModule {}

import { Injectable } from '@angular/core';
import { ActivatedRouteSnapshot, Resolve } from '@angular/router';
import { delay, finalize, map, switchMap, tap, timeout } from 'rxjs/operators';
import { CrudService } from '../services/http/crud-service.service';
import { UiHelper } from '../services/helpers/toast.service';
import { Plan } from '../models/plan';
import { AuthService } from '../services/auth.service';

@Injectable({
  providedIn: 'root',
})
export class PlanResolver implements Resolve<any> {
  constructor(
    private crud: CrudService,
    private auth: AuthService,
    private ui: UiHelper
  ) {}
  resolve(route: ActivatedRouteSnapshot) {
    let plan: Plan;
    const id = route.paramMap.get('id');
    this.ui.presentLoading(id);
    return this.crud.getPlanById(id).pipe(
      switchMap((plan) =>
        this.auth.getUserById(plan.createdBy).pipe(
          map((res) => ({ ...plan, creationUser: res })),
          finalize(() => this.ui.dismissLoading(id))
        )
      )
    );
  }
}

import {
  AfterViewInit,
  Component,
  ElementRef,
  OnInit,
  ViewChild,
} from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { NavController } from '@ionic/angular';
import { marker, latLng, MapOptions, tileLayer, Map, icon } from 'leaflet';
import { forkJoin } from 'rxjs';
import { finalize, tap } from 'rxjs/operators';
import { LatLng } from 'src/app/core/models/location';
import { Plan } from 'src/app/core/models/plan';
import { User } from 'src/app/core/models/usuario';
import { AuthService } from 'src/app/core/services/auth.service';
import { UiHelper } from 'src/app/core/services/helpers/toast.service';
import { CrudService } from 'src/app/core/services/http/crud-service.service';
import { LocationService } from 'src/app/core/services/http/location.service';

@Component({
  selector: 'app-plan-detail',
  templateUrl: './plan-detail.page.html',
  styleUrls: ['./plan-detail.page.scss'],
})
export class PlanDetailPage implements OnInit {
  layer;
  center: LatLng;
  firstTitleWord: string;
  options: MapOptions = {
    layers: [
      tileLayer('http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 18,
      }),
    ],
    zoom: 15,
    maxZoom: 18,
  };

  plan: Plan;
  user: User;
  constructor(
    private _ac: ActivatedRoute,
    private crud: CrudService,
    private _auth: AuthService,
    private nav: NavController,
    private ui: UiHelper
  ) {}

  ngOnInit() {
    this.plan = this._ac.snapshot.data?.plan;
    this.firstTitleWord = this.plan.title.split(' ')[0];
    this.plan = {
      ...this.plan,
      title: this.plan.title.split(' ').slice(1).join(' '),
    };
    this.updateCenter(this.plan);
    this.user = this._auth.loggedUser;
  }

  updateCenter({ location: { coordinates } }: Plan) {
    const greenIcon = icon({
      //add this new icon
      iconUrl: './../../../assets/icon/ubi.svg',
    });

    this.center = latLng(coordinates[0], coordinates[1]);
    this.options = { ...this.options, center: this.center };
    this.layer = marker([coordinates[0], coordinates[1]], { icon: greenIcon });
  }
  onMapReady(map: Map) {
    setTimeout(() => {
      map.invalidateSize();
    }, 0);
  }

  joinPlan() {
    const participants = this.plan.participants
      ? [...this.plan.participants, this.user.id]
      : [this.user.id];
    const acceptedPlans = this.user.acceptedPlans
      ? [...this.user.acceptedPlans, this.plan.id]
      : [this.plan.id];

    const plan = {
      ...this.plan,
      title: this.firstTitleWord + ' ' + this.plan.title,
      participants,
    };
    const user = { ...this.user, acceptedPlans };
    localStorage.setItem('loggedUser',JSON.stringify(user))
    forkJoin([
      this.crud.añadirUsuarioAPlan(plan),
      this.crud.añadirPlanAUsuario(user),
    ]).subscribe(
      () => {
        this.ui.presentToast(`Te has unido al plan: ${this.plan.title}!`);
        this.nav.back();
      },
      () => {
        this.ui.presentToast(
          'Ops, algo ha ido mal, inténtalo de nuevo o más tarde :S'
        );
      }
    );
  }
}

import { NgModule } from '@angular/core';
import { Routes, RouterModule } from '@angular/router';
import { PlanResolver } from 'src/app/core/resolvers/plan-resolver';

import { PlanDetailPage } from './plan-detail.page';

const routes: Routes = [
  {
    path: ':id',
    component: PlanDetailPage,
    resolve: {
      plan: PlanResolver
    }
  }
];

@NgModule({
  imports: [RouterModule.forChild(routes)],
  exports: [RouterModule],
})
export class PlanDetailPageRoutingModule {}

import { NgModule } from '@angular/core';
import { SharedModule } from 'src/app/shared/shared.module';
import { PlanDetailPageRoutingModule } from './plan-detail-routing.module';
import { PlanDetailPage } from './plan-detail.page';




@NgModule({
  imports: [SharedModule, PlanDetailPageRoutingModule],
  declarations: [PlanDetailPage],
})
export class PlanDetailPageModule {}
